        }

        /// <inheritdoc />
        public int CompareTo(object obj)
        {
            if(obj is null) throw new ArgumentNullException(nameof(obj));
            if(!(obj is Power objPower)) throw new ArgumentException("Expected type Power.", nameof(obj));

            return CompareTo(objPower);
        }

